import React from 'react';
import axios from 'axios';
import Header from '../Components/Header';
import Footer from '../Components/Footer';

import Category from './Category';
import Content from './Content';
import Spinner from 'react-spinner-material';

import { Route,Switch } from 'react-router-dom';
import './Home.css';
class Home extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            category: [],
            chooseID: 'LH001',
            products: [],
            totals: 0,
            currentPage: 1,
            isLoading: false
        }
    }

    async componentDidMount(){
        console.log('did mount')
        this.setState({isLoading: true})
        var response = await fetch('/api/category');
        var category = await response.json();

        var response_1 = await fetch(`/api/producttype/${this.state.chooseID}/1`);
        var products = await response_1.json();

        var response_2 = await fetch(`/api/totalproduct/${this.state.chooseID}`);
        var totals = await response_2.json();

        this.setState({
            category: category,
            products: products,
            totals: totals,
            isLoading: false
        })
    }

    onChoose = (maloaihang) => {
        this.setState({
            chooseID: maloaihang
        })
    }

    changeCurrentPage = (numPage) => {
        console.log(numPage)
    }

    componentDidUpdate(prevProps, prevState, snapshot){
        console.log(snapshot)
    }

    render(){
        console.log('home render')
        return(
            this.state.isLoading ?
             <p>Loading ...</p>
            :
            <div className="row1 body">
                    <Category category={this.state.category} onChoose={this.onChoose} />
                    <Content chooseID={this.state.chooseID} products={this.state.products} totals={this.state.totals} currentPage={this.state.currentPage} changeCurrentPage={this.changeCurrentPage}/>
            </div>
        );
    }
}

export default Home;
