import React from 'react';
import axios from 'axios';
import Header from '../Components/Header';
import Footer from '../Components/Footer';
import Category from './Category';
import Content from './Content';
import { Route,Switch } from 'react-router-dom';
import './Home.css';
class Home extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            category: [],
            chooseID: 'LH001'
        }
    }

    // componentDidMount(){
        
    //     let data = localStorage.getItem('category');
    //     if(data == null || data.length === 0){
    //         console.log('local')
    //         axios.get('/api/category')
    //         .then(res => {
    //             console.log(res.data)
    //             this.setState({
    //                 category: res.data
    //             })
    //             localStorage.setItem('category', JSON.stringify(res.data))
    //         })
    //         .catch(error => console.log(error));
    //     }else{
    //         this.setState({
    //             category: JSON.parse(data)
    //         })
    //     }
        
    // }

    componentDidMount(){
        console.log('did mount')
        axios.get('/api/category')
            .then(res => {
                this.setState({
                    category: res.data
                })
            })
            .catch(error => console.log(error));

        axios.get('/api/products')
            .then(res => {
                console.log(res.data)
            })
            .catch(error => console.log(error));
    }

    onChoose = (maloaihang) => {
        this.setState({
            chooseID: maloaihang
        })
    }

    render(){
        console.log('home render')
        return(
            <div className="row1 body">
                {React.memo(() => <Category category={this.state.category} chooseID={this.state.chooseID} onChoose={this.onChoose} />)}
                <Content chooseID={this.state.chooseID} />
            </div>
            
        );
    }
}


export default Home;
