import React from 'react';
class Category extends React.Component{
    constructor(props) {
        super(props)
    }

    onChoose = (maloaihang) => {
        // console.log(maloaihang);
        this.props.onChoose(maloaihang);
    }

    render(){
        let category = this.props.category.map(e => {
            // e.TEN_DANH_MUC = e.TEN_DANH_MUC.substring(1, e.TEN_DANH_MUC.length - 1);
            let categoryItem = e.categoryItem.map(item => {
                // item.TEN_LOAI = item.TEN_LOAI.substring(1, item.TEN_LOAI.length - 1);
                return(
                    <li key={item._id} onClick={() => this.onChoose(item.MA_LOAI_HANG)}>{item.TEN_LOAI}</li>
                )
            })

            return(
                <li key = {e._id}>
                    <ul>
                        {e.TEN_DANH_MUC}
                        <li>
                            {categoryItem}
                        </li>
                    </ul>
                </li>
            )
        })

        return(
            <div className="col-3 category">
                <div className="card border-secondary mb-3">
                    {category}
                </div>
            </div>
        )
    }
}

export default Category;
